/** * jssSlider v1.1.0 * Copyright 2019-2020 Serjio Painter * written during the spring heyday of nature * Licensed under MIT (http://opensource.org/licenses/MIT) */;$(document).ready(function(){var slider = $('.slider');var slide = 'li';var transition_time = 1000;var time_between_slides = 4000; 
function slides(){return slider.find(slide);}var j=1;slides().eq(0).css({"top":0+'px'});slides().eq(0).css({"left":0+'px'});slides().eq(0).css({"z-index":1001});
var  count_elements=slider.find(slide).length;var i=0;/*slider.find(slide + '.active').index()*/
for(j=1;j<count_elements;j++){slides().eq(j).css({"top":0+'px'});slides().eq(j).css({"left":700+'px'});/*slides().eq(j).fadeOut(transition_time);*/
slides().eq(j).css({"z-index":0});}function  ShowSlides(){if(i==0){slider.find(slide).eq(i).animate({'left':700+'px'},transition_time);slider.find(slide).eq(i).css({"z-index":0});slider.find(slide).eq(i+1).animate({'left':0+'px'},transition_time);
slider.find(slide).eq(i+1).css({"z-index":1001});for(j=2;j<count_elements-1;j++){slider.find(slide).eq(j).animate({'left':700+'px'},transition_time);
slider.find(slide).eq(j).css({"z-index":0});}}if((i>0)&&(i<count_elements-1)){for(k=0;k<i+1;k++){slider.find(slide).eq(k).animate({'left':700+'px'},transition_time);slider.find(slide).eq(k).css({"z-index":0});}slider.find(slide).eq(i+1).animate({'left':0+'px'},transition_time);slider.find(slide).eq(i+1).css({"z-index":1001});for(k=i+2;k<count_elements-1;k++){slider.find(slide).eq(k).animate({'left':700+'px'},transition_time);
slider.find(slide).eq(k).css({"z-index":0});}}if(i==count_elements-1) {slider.find(slide).eq(0).animate({'left':0+'px'},transition_time);slider.find(slide).eq(0).css({"z-index":1001});for(k=1;k<count_elements;k++){slider.find(slide).eq(k).animate({'left':700+'px'},transition_time);slider.find(slide).eq(k).css({"z-index":0});}}if(count_elements-1==i){i = 0;}else{i++;}}var  Interval = setInterval(function(){ShowSlides()}, time_between_slides+transition_time);});